mapper.select:
SELECT item.id, item.code, item.serve_type_id, type.name AS serve_type_name, item.name, item.serve_item_icon, item.img, item.unit, item.description, item.detail_img, item.reference_price, item.sort_num, item.active_status, item.create_time, item.update_time FROM serve_item AS item LEFT JOIN serve_type AS type ON item.serve_type_id = type.id
and item.serve_type_id=#{serveTypeId}
and item.name like concat('%',#{name},'%')
and item.active_status=#{activeStatus}
